Matrix amatrix,bmatrix,cmatrix;
amatrix = new Matrix(8,2);
bmatrix = new Matrix(8,1);

amatrix.set(0,0,1);
amatrix.set(1,0,1);
amatrix.set(2,0,1);
amatrix.set(3,0,1);
amatrix.set(4,0,1);
amatrix.set(5,0,1);
amatrix.set(6,0,1);
amatrix.set(7,0,1);

amatrix.set(0,1,80);
amatrix.set(1,1,220);
amatrix.set(2,1,140);
amatrix.set(3,1,120);
amatrix.set(4,1,180);
amatrix.set(5,1,100);
amatrix.set(6,1,200);
amatrix.set(7,1,160);

bmatrix.set(0,0,0.6);
bmatrix.set(1,0,6.70);
bmatrix.set(2,0,5.30);
bmatrix.set(3,0,4.00);
bmatrix.set(4,0,6.55);
bmatrix.set(5,0,2.15);
bmatrix.set(6,0,6.60);
bmatrix.set(7,0,5.75);

cmatrix = amatrix.solve(bmatrix);
amatrix.print(8,5);
bmatrix.print(8,5);
cmatrix.print(8,5);
